mapper.delete:
		DELETE FROM tb_user_character_map
        WHERE user_id = #{userId}
        AND character_id = #{characterId}

		DELETE FROM tb_user_character_skill_map
		WHERE user_id = #{userId}
        AND character_id = #{characterId}

DELETE FROM tb_user_character_skill_map
WHERE user_id = #{userId}
AND character_id = #{characterId}
AND skill_id = #{skillId}
